function editProfilePage_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'doneEditButton': 'editProfilePage_doneEditButton',
        'mobilelabel_5': 'editProfilePage_mobilelabel_5',
        'fullNameInput': 'editProfilePage_fullNameInput',
        'mobilelabel_6': 'editProfilePage_mobilelabel_6',
        'birthdayField': 'editProfilePage_birthdayField',
        'mobilelabel_8': 'editProfilePage_mobilelabel_8',
        'cityField': 'editProfilePage_cityField',
        'mobilelabel_10': 'editProfilePage_mobilelabel_10',
        'mobiletextinput_11': 'editProfilePage_mobiletextinput_11',
        'mobilelabel_9': 'editProfilePage_mobilelabel_9',
        'aboutMeField': 'editProfilePage_aboutMeField'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["editProfilePage_restservice4_onsuccess_mapping_0"] = {
        "homeScreen": "editProfilePage",
        "directions": [

        {
            "from_name": "restservice4",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "editProfilePage",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['body']['data']['birthday']",
                "target": "$['birthdayField:defaultDateValue']"

            },

            {

                "source": "$['body']['data']['last_name']",
                "target_transformation": function(value, element) {
                    var fullName = sessionStorage.firstName + " " + value;
                    return fullName;
                },
                "target": "$['fullNameInput:text']"

            },

            {

                "source": "$['body']['data']['location']",
                "target": "$['cityField:text']"

            }

            ]
        },

        {
            "from_name": "restservice4",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "firstName",
            "to_type": "SESSION_STORAGE",

            "mappings": [

            {

                "source": "$['body']['data']['first_name']",
                "target": "$"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["editProfilePage_restservice4_onbeforesend_mapping_0"] = {
        "homeScreen": "editProfilePage",
        "directions": [

        {
            "from_name": "userFbId",
            "from_type": "LOCAL_STORAGE",

            "to_name": "restservice4",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "headers": {},
                "parameters": {},
                "body": null
            },

            "mappings": [

            {

                "source": "$",
                "target": "$['parameters']['me']"

            }

            ]
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.restservice4 = Apperyio.datasources.restservice4 = new Apperyio.DataSource(GetMeService, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["editProfilePage_restservice4_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("editProfilePage");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["editProfilePage_restservice4_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });

    Apperyio.CurrentScreen = 'editProfilePage';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var editProfilePage_onLoad = function() {
            editProfilePage_elementsExtraJS();

            try {
                restservice4.execute({});
            } catch (e) {
                console.error(e);
                hideSpinner();
            };

            editProfilePage_deviceEvents();
            editProfilePage_windowEvents();
            editProfilePage_elementsEvents();
        };

    // screen window events


    function editProfilePage_windowEvents() {

        $('#editProfilePage').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#editProfilePage').on({
            pageshow: function(event) {
                try {
                    $a.c15r("aboutMeField", "set", "text", $a.storage["aboutMe"].get("$"))
                } catch (e) {
                    console.error(e)
                };
            },
        });

    };

    // device events


    function editProfilePage_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function editProfilePage_elementsExtraJS() {
        // screen (editProfilePage) extra code

        /* birthdayField */

        editProfilePage_birthdayField_selector = "#editProfilePage_birthdayField";
        editProfilePage_birthdayField_dataPickerOptions = {
            dateFormat: "mm/dd/yy",
            firstDay: 0,

            maxDate: new Date(""),

            minDate: new Date(""),

            dayNamesMin: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
            monthNames: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],
            showOtherMonths: true
        };

        editProfilePage_birthdayField_dataPickerOptions.defaultDate = new Date("04/11/2015");

        Apperyio.__registerComponent('birthdayField', new Apperyio.ApperyMobileDatePickerComponent("editProfilePage_birthdayField", editProfilePage_birthdayField_dataPickerOptions));

    };

    // screen elements handler


    function editProfilePage_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#editProfilePage_mobileheader [name="doneEditButton"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('profilePage', {
                        reverse: false
                    });
                    try {
                        $a.storage["aboutMe"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('aboutMeField', this), 'get', 'text'))
                    } catch (e) {
                        console.error(e)
                    };

                }
            },
        }, '#editProfilePage_mobileheader [name="doneEditButton"]');

    };

    $(document).off("pagebeforeshow", "#editProfilePage").on("pagebeforeshow", "#editProfilePage", function(event, ui) {
        Apperyio.CurrentScreen = "editProfilePage";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    editProfilePage_onLoad();
};

$(document).off("pagecreate", "#editProfilePage").on("pagecreate", "#editProfilePage", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    editProfilePage_js();
});