    // On Load
    var dashboard_onLoad = function() {
            dashboard_elementsExtraJS();

            $.ajax({
                type: 'POST',
                url: "http://ec2-54-211-26-30.compute-1.amazonaws.com/api/register/",
                data: {
                    token: localStorage.access_token,
                    me: localStorage.userFbId
                },
                success: function(data) {

                },
            });
            try {
                restservice3.execute({});
            } catch (e) {
                console.error(e);
                hideSpinner();
            };

            dashboard_deviceEvents();
            dashboard_windowEvents();
            dashboard_elementsEvents();
        };

    // screen window events


    function dashboard_windowEvents() {

        $('#dashboard').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#dashboard').on({
            pageshow: function(event) {
                try {
                    getNearbyService.execute({});
                } catch (e) {
                    console.error(e);
                    hideSpinner();
                };
            },
        });

    };

    // device events


    function dashboard_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function dashboard_elementsExtraJS() {
        // screen (dashboard) extra code

    };

    // screen elements handler


    function dashboard_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#dashboard_mobileheader [name="toggleProfileButton"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('profilePage', {
                        transition: 'flip',
                        reverse: false
                    });

                }
            },
        }, '#dashboard_mobileheader [name="toggleProfileButton"]');

        $(document).off("click", '#dashboard_mobilecontainer [name="setDiscoveryButton"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('discoveryPreferencesPage', {
                        reverse: false
                    });

                }
            },
        }, '#dashboard_mobilecontainer [name="setDiscoveryButton"]');

        $(document).off("click", '#dashboard_mobilecontainer [name="findSomeoneNearby"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        getNearbyAddressService.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };

                }
            },
        }, '#dashboard_mobilecontainer [name="findSomeoneNearby"]');

        $(document).off("click", '#dashboard_mobilecontainer [name="personPhoto"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        $a.storage["profileFirstName"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('firstNameLabel', this), 'get', 'text'))
                    } catch (e) {
                        console.error(e)
                    };
                    try {
                        $a.storage["profileAge"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('ageLabel', this), 'get', 'text'))
                    } catch (e) {
                        console.error(e)
                    };
                    try {
                        $a.storage["profileLocation"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('cityLabel', this), 'get', 'text'))
                    } catch (e) {
                        console.error(e)
                    };
                    try {
                        $a.storage["profilePictureUrl"].update("$", $a.c15r($a.UIHandler.resolveGeneratedComponent('personPhoto', this), 'get', 'image'))
                    } catch (e) {
                        console.error(e)
                    };
                    try {
                        $a.storage["profileAboutMe"].update("$", "Hello, this is me.")
                    } catch (e) {
                        console.error(e)
                    };
                    Apperyio.navigateTo('otherProfilePage', {
                        reverse: false
                    });

                }
            },
        }, '#dashboard_mobilecontainer [name="personPhoto"]');

    };

