function mapViewPage_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'googlemap_2': 'mapViewPage_googlemap_2',
        'marker_3': 'mapViewPage_marker_3'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["mapViewPage_geolocation2_onsuccess_mapping_0"] = {
        "homeScreen": "mapViewPage",
        "directions": [

        {
            "from_name": "geolocation2",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "mapViewPage",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['data']['coords']['latitude']",
                "target": "$['googlemap_2:latitude']"

            },

            {

                "source": "$['data']['coords']['longitude']",
                "target": "$['googlemap_2:longitude']"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["mapViewPage_geolocation2_onbeforesend_mapping_0"] = {
        "homeScreen": "mapViewPage",
        "directions": [

        {

            "to_name": "geolocation2",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "data": {
                    "options": {
                        "maximumAge": 3000,
                        "timeout": 5000,
                        "enableHighAccuracy": true,
                        "watchPosition": false
                    }
                }
            },

            "mappings": []
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.geolocation2 = Apperyio.datasources.geolocation2 = new Apperyio.DataSource(GeolocationService, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["mapViewPage_geolocation2_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("mapViewPage");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["mapViewPage_geolocation2_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });

    Apperyio.CurrentScreen = 'mapViewPage';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var mapViewPage_onLoad = function() {
            mapViewPage_elementsExtraJS();

            mapViewPage_deviceEvents();
            mapViewPage_windowEvents();
            mapViewPage_elementsEvents();
        };

    // screen window events


    function mapViewPage_windowEvents() {

        $('#mapViewPage').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function mapViewPage_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function mapViewPage_elementsExtraJS() {
        // screen (mapViewPage) extra code

        /* googlemap_2 */

        $("[name = 'googlemap_2']").wrap("<div/>");
        $("[name = 'googlemap_2']").parent().css("margin-left", $("[name = 'googlemap_2']").css("margin-left"));
        $("[name = 'googlemap_2']").parent().css("margin-right", $("[name = 'googlemap_2']").css("margin-right"));
        $("[name = 'googlemap_2']").css("margin-left", '0');
        $("[name = 'googlemap_2']").css("margin-right", '0');

        var googlemap_2_options = {
            markerSourceName: "googlemap_2_markers",
            latitude: "",
            longitude: "",
            address: "",
            zoom: 10,
            showLocationMarker: false
        }

        Apperyio.__registerComponent('googlemap_2', new Apperyio.ApperyMapComponent("googlemap_2", googlemap_2_options));
        $("[name='googlemap_2_markers'] [apperytype='marker']").attr("reRender", "googlemap_2");
        $(":mobile-pagecontainer").off("pagecontainershow.mapViewPage_mobilecontainer").on("pagecontainershow.mapViewPage_mobilecontainer", function(event, ui) {
            if (($('#mapViewPage_googlemap_2', ui.toPage).length > 0) && (Apperyio('googlemap_2') != undefined)) {
                Apperyio('googlemap_2').refresh();
            }
        });

    };

    // screen elements handler


    function mapViewPage_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

    };

    $(document).off("pagebeforeshow", "#mapViewPage").on("pagebeforeshow", "#mapViewPage", function(event, ui) {
        Apperyio.CurrentScreen = "mapViewPage";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    mapViewPage_onLoad();
};

$(document).off("pagecreate", "#mapViewPage").on("pagecreate", "#mapViewPage", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    mapViewPage_js();
});